<style lang="scss">
// breakpoints for custom grid

@mixin breakpoint($point) {
    // mobile first
    @if $point == bpXLmin {
        @media (min-width: $bpXL) { @content; }
    }
    @if $point == bpLmin {
        @media (min-width: $bpL) { @content; }
    }
    @if $point == bpMmin {
        @media (min-width: $bpM) { @content; }
    }
    @if $point == bpSmin {
        @media (min-width: $bpS) { @content; }
    }
    @if $point == bpXSmin {
        @media (min-width: $bpXS) { @content; }
    }

    // desktop first
    @if $point == bpXLmax {
        @media (max-width: $bpXL) { @content; }
    }
    @if $point == bpLmax {
        @media (max-width: $bpL) { @content; }
    }
    @if $point == bpMmax {
        @media (max-width: $bpM) { @content; }
    }
    @if $point == bpSmax {
        @media (max-width: $bpS) { @content; }
    }
    @if $point == bpXSmax {
        @media (max-width: $bpXS) { @content; }
    }

    // aspect first
    @if $point == bpPL {
        @media screen and (min-width: $bpS) and (min-height: 600px) { @content; }
    }
    @if $point == bpPLmax {
        @media screen and (max-width: $bpS) and (max-height: 600px) { @content; }
    }

}

// z-index elevation

$list: layer-1, layer-2, layer-3, layer-4, layer-5, layer-6, layer-7, layer-8, layer-9, layer-10, layer-11, layer-12, layer-13, layer-14, layer-15, layer-16, layer-17, layer-18, layer-19, layer-20, layer-21, layer-22, layer-23, layer-24;

@function elevation($list, $elevation, $offset) {

  $z-index: (index($list, $elevation)*100) + $offset ;

  @if $z-index {
    @return $z-index;
  }

  @warn 'There is no item "#{$elevation}" in this list; choose one of: #{$list}';
  @return null;
}

@mixin elevation($elevation, $offset){
  z-index: elevation($list,$elevation, $offset);
}

  .comp-debug {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    @include elevation(layer-24,0);
    overflow: hidden;
    .grid {
        box-sizing: border-box;
        float: left;
        width: 25%;
        padding-left: 8px;
        padding-right: 8px;
        @include breakpoint(bpXSmin) {
            width: 16.66667%;
            float: left;
            padding-left: 8px;
            padding-right: 8px;
        }
        @include breakpoint(bpMmin) {
            width: 7.14286%;
            padding-left: 8px;
            padding-right: 8px;
        }
        .column {
            background-color: red;
            opacity: 0.25;
            height: 4000px;
        }
        border-right: 1px solid red;
        &:first-child {
            border-left: 1px solid red;
        }
    }
    .row {
        box-sizing: border-box;
        opacity: 0.25;
        border-bottom: 1px solid red;
        padding: 12px 0;
        margin-top: -1px;
        .module {
            background-color: blue;
            opacity: 0.15;
            height: 60px;
        }
    }
    .baseline {
        box-sizing: border-box;
        opacity: 0.25;
        height: 12px;
        border-bottom: 1px dotted gray;
    }
}


</style>
